namespace MageINIC\Testimonial\ViewModel;

use Magento\Framework\View\Element\Block\ArgumentInterface;
use MageINIC\Testimonial\Helper\Data;

/**
 * Provides the user data to fill the form.
 */
class GeneralConfigurationData implements ArgumentInterface
{
    /**
     * @var Data
     */
    private Data $helperData;

    /**
     * GeneralConfigurationData Constructor.
     *
     * @param Data $helperData
     */
    public function __construct(
        Data $helperData
    ) {
        $this->helperData = $helperData;
    }

    /**
     * Retrieve Helper Functions.
     *
     * @return Data
     */
    public function getHelperData(): Data
    {
        return $this->helperData;
    }
}
